import React, { Component, NativeModules } from 'react';
import ReactNative, {requireNativeComponent } from 'react-native';

const nativeComponents = {};

const hyRequireNativeComponent = (viewName,
    componentInterface,
    extraConfig) => {
    
        let component = nativeComponents[viewName];
        if (!component) {
            component = requireNativeComponent(viewName, componentInterface, extraConfig);
            nativeComponents[viewName] = component;
        }
        return component;
}

module.exports = hyRequireNativeComponent