const { execSync } = require('child_process')
const { HINT } = require('../utils/utils.js')

function validateNodeVersion() {
  try {
    const version = execSync('node -v').toString()
    HINT(`HYEXT-H5Game: 检查 node 版本: ${version}`)
    const [major] = version.split('.')
    return major === 'v10'
  } catch (err) {
    return false
  }
}

module.exports = {
  validateNodeVersion,
}
