function addScriptToJson(pkgContent = {}, scriptsObject = {}) {
  const { scripts = {} } = pkgContent

  Object.keys(scriptsObject).forEach(scriptName => {
    if (scripts[scriptName]) {
      // console.log(`相同的 script 名称: ${scriptName} 已经存在`)
      return
    }

    scripts[scriptName] = scriptsObject[scriptName]
  })

  return {
    ...pkgContent,
    scripts,
  }
}

module.exports = {
  addScriptToJson,
}
