/**
* log组件
* @namespace HYRNLog
*/

import {
	NativeModules,
} from 'react-native';

/**
 * 打log info级别
 * @function info
 * @memberof HYRNLog
 * @param {string} tag     - log的标签
 * @param {string} message - log详情
 * @example
 * HYRNLog.info("TAG","info");
 */

/**
 * 打log error级别
 * @function error
 * @memberof HYRNLog
 * @param {string} tag     - log的标签
 * @param {string} message - log详情
 * @example
 * HYRNLog.error("TAG","info");
 */

/**
 * 打log debug级别
 * @function debug
 * @memberof HYRNLog
 * @param {string} tag     - log的标签
 * @param {string} message - log详情
 * @example
 * HYRNLog.debug("TAG","info");
 */

const NativeLog = NativeModules.KLog;

export default {
	info: function (...args) {
		let str = args ? args.toString() : '';
		__DEV__ && console.info('HYExt', str);
		NativeLog.info('HYExt', str);
	},
	warn: function (...args) {
		let str = args ? args.toString() : '';
		__DEV__ && console.info('HYExt', str);
		NativeLog.info('HYExt', str ? str : "");
	},
	debug: function (...args) {
		let str = args ? args.toString() : '';
		__DEV__ && console.debug('HYExt', str);
		__DEV__ && NativeLog.debug('HYExt', str);
	},
	error: function (...args) {
		let str = args ? args.toString() : '';
		__DEV__ && console.error('HYExt', str);
		NativeLog.error('HYExt', str);
	}
};