#!/usr/bin/env node

const commander = require('commander')
const chalk = require('chalk')
const packageJson = require('../package.json')

const program = new commander.Command(packageJson.name)
  .usage(`'${packageJson.name} run' is deprecated - use '${packageJson.name} start' instead`)
  .allowUnknownOption()
  .parse(process.argv);

console.log(`  ${chalk.cyan(packageJson.name, 'run')} is deprecated - use ${chalk.cyan(packageJson.name, 'start')} instead.`);

process.exit(0);
