/**
* HYExtBackend
* @namespace HYExtBackend
*/
import {
    NativeModules
} from 'react-native';

const NativeExtBackend = NativeModules.HYExtBackend;

class ExtBackend {

    async commonQuery(params) {
        try {
            let responseJson = await NativeExtBackend.commonQuery(params);
            return JSON.parse(responseJson);
        }
        catch (error) {
            throw (error);
        }
    }
}

const HYExtBackend = new ExtBackend();

export default HYExtBackend;