import {
    NativeModules,
}from 'react-native';

const NativeExtView = NativeModules.HYExtView;

class ExtView{
    createSubView(param){
        return NativeExtView.createSubView(param);
    }

    destroySubView(param){
        return NativeExtView.destroySubView(param);
    }
     
    hideCurrentView(){
        return NativeExtView.hideCurrentView();
    }

    hideSubView(param){
        return NativeExtView.hideSubView(param);
    }

    /**
     * 
     * @param {*} param 
     * {
     *    subViewId: 'xxx'
     *  }
     *
     */
    showSubView(param){
        return NativeExtView.showSubView(param);
    }
}

const HYExtView = new ExtView();
export default HYExtView;
